mapper.insert:
INSERT INTO /* [Token.xml][]insertToken][거래선 사용자에 인증키 등록]*/
MG_TOKEN_BASE (
	  USR_NO
	, TOKEN 
	, USE_YN 
	, CONF_STAT_CD 
	, CRET_DTIME 
	, CRET_USR_NO 
	, CRET_PGM_ID 
	, CHG_DTIME 
	, CHG_USR_NO 
	, CHG_PGM_ID
) VALUES (
	  #{usrNo}
	, #{token}
	, 'Y'
	, '02'
	, SYSDATE
	, #{loginUsrNo}
	, #{pgmId}
	, SYSDATE
	, #{loginUsrNo}
	, #{pgmId}
)

mapper.update:
		UPDATE /* [Token.xml][인증키 갱신] */
    		MG_TOKEN_BASE
    	SET
			  TOKEN = #{token}
			, CHG_DTIME  = SYSDATE
			, CHG_USR_NO = #{loginUsrNo}
			, CHG_PGM_ID = #{pgmId}
		WHERE
			USR_NO = #{usrNo}

		UPDATE /* [Token.xml][인증키 사용유무 수정] */
    		MG_TOKEN_BASE
    	SET
    		  USE_YN = #{useYn}
			, CHG_DTIME  = SYSDATE
			, CHG_USR_NO = #{loginUsrNo}
			, CHG_PGM_ID = #{pgmId}
		WHERE
			TOKEN = #{token}
